import breeze.linalg.{DenseVector => BDV}
import org.apache.spark.ml.linalg.{Vector, Vectors}

def word2BDV(word: String) = {
    val vector = model.getVectors.where(s"word = '${word}'").first.getAs[Vector](1)
    val bdv = BDV(vector.toArray)
    bdv
}

def vectorOp(a: String, b: String, c: String) = {
    val bdv = word2BDV(a) - word2BDV(b) + word2BDV(c)
    val vector = Vectors.dense(bdv.toArray)
    model.findSynonyms(vector, 10)
}

